if($params['selected'])
{
$result["selected"] = $params['selected'];
$c=0;
foreach($result["selected"] as $v)
{
$sql = "UPDATE `listings` SET `status`='1' WHERE `listingid`='".$v."'";
CustomQuery($sql);
/////
$sql_a = "select date_posted from `listings` WHERE `listingid`='".$v."'";
